INSERT INTO `newses` (`id`, `title`, `fa_title`, `description`, `fa_description`, `image`, `alt`, `alt_fa`, `created_at`, `updated_at`) VALUES
(2, 'The 12th International poultry and Aquatic Exhibitions', 'دوازدهمین دوره نمایشگاه های بین المللی دام و طیور و آبزیان در شهر مقدس مشهد', '<p class=\"MsoNormal\" align=\"right\" style=\"text-align: left;\"><span lang=\"EN-US\">The 12th International poultry and Aquatic\r\nExhibitions was held in Mashhad, Holy City, Mashhad, from September 27 to 30,2016.\r\nKutlusan is proud to be present at the exhibition in a booth with a total area\r\nof 20 square meters serving the poultry houses in Khorasan province.<o:p></o:p></span></p><p class=\"MsoNormal\" align=\"right\" style=\"text-align: left;\"><span lang=\"EN-US\">&nbsp;</span></p><p>\r\n\r\n\r\n\r\n</p><p class=\"MsoNormal\" align=\"right\" style=\"text-align: left;\"><span lang=\"EN-US\">Considering the increased number of poultry houses\r\nin this province, welcoming the significant exhibit and interest in\r\nmechanization among poultry houses were clearly seen.</span><span lang=\"FA\" dir=\"RTL\" style=\"font-family:&quot;Arial&quot;,sans-serif;mso-ascii-font-family:Calibri;\r\nmso-ascii-theme-font:minor-latin;mso-hansi-font-family:Calibri;mso-hansi-theme-font:\r\nminor-latin;mso-bidi-font-family:Arial;mso-bidi-theme-font:minor-bidi;\r\nmso-bidi-language:FA\"><o:p></o:p></span></p>', '<p class=\"MsoNormal\" align=\"right\" style=\"text-align:right\"><span lang=\"FA\" dir=\"RTL\" style=\"\">دوازدهمین دوره از نمایشگاه های بین المللی دام و طیور و آبزیان در تاریخ 6\r\nالی 9 مهر ماه 1395 در شهر مقدس مشهد برگزار گردید و شرکت کوتلوسان افتخار دارد با\r\nشرکت در این نمایشگاه در غرفه ای به متراژ 20 متر مربع به ارائه خدمت به مرغداران\r\nعزیز استان خراسان بپردازد.<o:p></o:p></span></p><p>\r\n\r\n</p><p class=\"MsoNormal\" align=\"right\" style=\"text-align:right\"><span lang=\"FA\" dir=\"RTL\" style=\"\">با توجه به ازدیاد سالنهای مرغداری در این استان ، استقبال از نمایشگاه قابل\r\nتوجه و علاقه به مکانیزاسیون در بین مرغداران عزیز به وضوح مشاهده گردید.<font face=\"Arial, sans-serif\"><o:p></o:p></font></span></p>', '152334796212 mashhad.jpg', 'The 12th International poultry and Aquatic Exhibitions', 'دوازدهمین دوره از نمایشگاه های بین المللی دام و طیور و آبزیان در شهر مقدس مشهد', '2018-04-10', '2018-04-10'),
(3, 'The 15th international exhibition of livestock and poultry', 'پانزدهمین نمایشگاه بین المللی صنعت دام و طیور و آبزیان تبریز ', '<p class=\"MsoNormal\" align=\"right\" style=\"text-align: left;\"><span lang=\"EN-US\" style=\"font-size: 14.6667px;\">The 15th international exhibition of livestock and poultry and aquaculture industry in Tabriz on May&nbsp;</span><span dir=\"RTL\" style=\"font-size: 14.6667px;\"></span><span dir=\"RTL\" style=\"font-size: 14.6667px;\"></span><span lang=\"FA\" dir=\"RTL\" style=\"font-size: 14.6667px; font-family: Arial, sans-serif;\"><span dir=\"RTL\"></span><span dir=\"RTL\"></span>17</span><span dir=\"LTR\" style=\"font-size: 14.6667px;\"></span><span dir=\"LTR\" style=\"font-size: 14.6667px;\"></span><span lang=\"EN-US\" style=\"font-size: 14.6667px;\"><span dir=\"LTR\"></span><span dir=\"LTR\"></span>-</span><span dir=\"RTL\" style=\"font-size: 14.6667px;\"></span><span dir=\"RTL\" style=\"font-size: 14.6667px;\"></span><span lang=\"FA\" dir=\"RTL\" style=\"font-size: 14.6667px; font-family: Arial, sans-serif;\"><span dir=\"RTL\"></span><span dir=\"RTL\"></span>20</span><span dir=\"LTR\" style=\"font-size: 14.6667px;\"></span><span dir=\"LTR\" style=\"font-size: 14.6667px;\"></span><span lang=\"EN-US\" style=\"font-size: 14.6667px;\"><span dir=\"LTR\"></span><span dir=\"LTR\"></span>,2017 &nbsp;was held in the permanent exhibition hall of the province in a 25 sq.m. pavilion, welcomed by industry activists. During this exhibition, we succeeded in introducing cages for laying hens, broiler chickens and pullets.</span><br></p>', '<p class=\"MsoNormal\" align=\"right\" style=\"text-align:right\"><span style=\"font-size: 14.6667px;\">پانزدهمین نمایشگاه بین المللی صنعت دام و طیور و آبزیان تبریز در تاریخ 27 الی30 اردیبهشت 1396 در محل دائمی نمایشگاه های این استان در غرفه ایی به متراژ 25 متر مربع برگزار گردید و با استقبال فعالین این صنعت مواجه شد. که در طی این نمایشگاه موفق به معرفی قفسهای مرغ تخمگذار، مرغ گوشتی و پولت شده‌ایم. </span><br></p>', '1523348926tabriz namayeshgah.png', 'The 15th international exhibition of livestock and poultry', 'پانزدهمین نمایشگاه بین المللی صنعت دام و طیور و آبزیان تبریز', '2018-04-10', '2018-11-12'),
(4, 'the 14th International Exhibition of Livestock and Poultry ', 'چهاردهمین دوره نمایشگاه بین المللی دام و طیور وآبزیان آذربایجان شرقی', '<p class=\"MsoNormal\" align=\"right\" style=\"text-align: left;\"><span style=\"font-size: 14.6667px;\">Participation in the 14th International Exhibition of Livestock and Poultry and west Azarbaijan is one of the other activities of Kutlusan Co., which is necessary for holding the exhibition due to the number of projects in this province. During the 4-day exhibition of this course, in addition to&nbsp; we have succeeded in introducing more and more products from the Kutlusan Company to the goal of serving the livestock industry in the country.</span><br></p>', '<p class=\"MsoNormal\" align=\"right\" style=\"text-align:right\"><span style=\"font-size: 14.6667px;\">شرکت در چهاردهمین دوره نمایشگاه بین المللی دام و طیور وآبزیان آذربایجان شرقی از دیگر فعالیتهای شرکت کوتلوسان می باشد که با توجه به تعداد پروژه های شرکت در این استان برگزاری این نمایشگاه ضروری است. در طول نمایشگاه 4 روزه این دوره موفق به معرفی هر چه بیشتر محصولات شرکت کوتلوسان شده و به هدف خدمت به صنعت دام و طیور کشور دست یافته ایم.</span><br></p>', '152335080914 tabriz.jpg', 'the 14th International Exhibition of Livestock and Poultry', 'چهاردهمین دوره نمایشگاه بین المللی دام و طیور وآبزیان آذربایجان شرقی', '2018-04-10', '2018-11-12'),
(5, '15th exhibition of international poultry in Isfahan', '  پانزدهمین دوره نمایشگاه بین المللی دام و طیور و دامپزشکی اصفهان 1396', '<p class=\"MsoNormal\" style=\"text-align: left; \"><span lang=\"EN-US\" style=\"font-size: 11pt;\">Kutlusan\r\nCompany participated in the exhibition of international exhibitions in Isfahan\r\nin order to assist the poultry and &nbsp;livestock industry in the country as well as\r\nto improve the level of poultry equipment. It was welcomed by visitors.</span><span style=\"font-size: 11pt;\">&nbsp;</span><br></p><p>\r\n\r\n\r\n\r\n\r\n\r\n</p><p class=\"MsoNormal\" align=\"right\" style=\"text-align: left;\"><span lang=\"EN-US\">The opening date of the exhibition was held from\r\nTuesday, 29/1/96 to Friday 1/2/96, for four days at the place of the\r\nInternational Exhibition of Isfahan.</span></p>', '<p class=\"MsoNormal\" align=\"right\" style=\"text-align:right\"><span lang=\"FA\" dir=\"RTL\" style=\"\">شرکت کوتلوسان در راستای کمک به صنعت دام و طیور کشور و همچنین ارتقای سطح\r\nتجهیزات پرورش طیور درنمایشگاه بین المللی اصفهان 96 حضور به عمل رسانید که با\r\nاستقبال خوب بازدید کنندگان همراه بود.<o:p></o:p></span></p><p>\r\n\r\n</p><p class=\"MsoNormal\" align=\"right\" style=\"text-align:right\"><span lang=\"FA\" dir=\"RTL\" style=\"\">تاریخ شروع نمایشگاه از سه شنبه مورخ 29/1/96 تا تاریخ جمعه 1/2/96 به مدت\r\nچهار روز در مکان نمایشگاه بین المللی اصفهان برگزار گردید.<font face=\"Arial, sans-serif\"><o:p></o:p></font></span></p>', '1523351624esfehan.jpg', '15th exhibition of international poultry in Isfahan', ' پانزدهمین دوره نمایشگاه بین المللی دام و طیور و دامپزشکی اصفهان 1396', '2018-04-10', '2018-11-12'),
(6, 'Oman agro-food exihibition', 'نمایشگاه مواد غذایی و کشاورزی عمان', '<p style=\"text-align: left; \">Agro Food Oman 2018 will be organized by Al Nimr Expo under the patronage of the Ministry of Agriculture &amp; Fisheries Wealth. It will be held from 7-9 May 2018 at the Oman Convention &amp; Exhibition Centre. The expo aims to link the sector as well as the related government organizations with industry experts and suppliers by sharing innovative ideas and solutions to transform the sector, showing the potential involved toward its contribution to the Sultanates growing economy.Novin Niroo Co. is proud to present a positive step in the field of food exports and expansion of markets in our beloved Iran, by attending the exhibition and presenting its potential and capabilities in the food industry and other related industries.<br></p>', '<p>نمایشگاه غذایی -کشاورزی سال ۲۰۱۸عمان تحت نظارت وزارت کشاورزی و شیلات سازماندهی شده است. این نمایشگاه از ۷ تا ۹ مه ۲۰۱۸ در مرکز نمایشگاه عمان برگذار شد. هدف از این نمایشگاه، مرتبط کردن این بخش و نیز سازمان‌های دولتی مرتبط با متخصصان صنعت و تامین کنندگان با به اشتراک گذاری ایده‌ها و راه‌حل‌ها برای تبدیل این بخش و نشان دادن پتانسیل دخیل در مشارکت آن در اقتصاد رو به رشد است. شرکت نوین نیرو مفتخر است با حضور خود در نمایشگاه و ارایه پتانسیل و توانمندی های خود در صنعت موادغذایی و دیگر صنایع مرتبط گامی مثبت در زمینه صادرات موادغذایی و گسترش بازارهای کشور عزیزمان ایران، بردارد.<br></p>', '1528006884Agro-Food-Oman-416x147.jpg', 'oman agro food 2018', 'نمایشگاه مواد غذایی عمان سال 2018', '2018-06-03', '2018-11-12');

